    private void SubmitBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SubmitBtnActionPerformed
        // TODO add your handling code here:
        String gender=null;
        if(MaleTF.isSelected()){
            gender="Male";
        }
        else if(FemaleTF.isSelected()){
            gender="Female";
        }
        
        String Btype = BTypeCMB.getSelectedItem().toString();
        
        InsertController.InsertPatient(FNameTF.getText(),LNameTF.getText(), NICTF.getText(), DOBTf.getText(), gender, AddressTF.getText(), ContactTF.getText(),Btype );
        
    }//GEN-LAST:event_SubmitBtnActionPerformed

