    public void clearField(){
        FNameTF.setText("");
        LNameTF.setText("");
        NICTF.setText("");
        DOBTf.setText("");
        MaleTF.setSelected(false);
        FemaleTF.setSelected(false);
        AddressTF.setText("");
        ContactTF.setText("");
        BTypeCMB.setSelectedItem(0);
    }
